import logo from "../logo_3.png";
import fullLogo from "../full_logo.png";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams,
} from "react-router-dom";
import { useEffect, useState } from "react";
import { useLocation } from "react-router";

import React from "react";
import "./navbar.css";

function Navbar() {
  const [Mobile, setMobile] = useState(false);

  const [connected, toggleConnect] = useState(false);
  const location = useLocation();
  const [currAddress, updateAddress] = useState("0x");

  async function getAddress() {
    const ethers = require("ethers");
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const addr = await signer.getAddress();
    updateAddress(addr);
  }

  function updateButton() {
    const ethereumButton = document.querySelector(".enableEthereumButton");
    ethereumButton.textContent = "Connected";
    ethereumButton.classList.remove("hover:bg-blue-70");
    ethereumButton.classList.remove("bg-blue-500");
    ethereumButton.classList.add("hover:bg-green-70");
    ethereumButton.classList.add("bg-green-500");
  }

  async function connectWebsite() {
    const chainId = await window.ethereum.request({ method: "eth_chainId" });
    if (chainId !== "0x5") {
      //alert('Incorrect network! Switch your metamask network to Rinkeby');
      await window.ethereum.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: "0x5" }],
      });
    }
    await window.ethereum
      .request({ method: "eth_requestAccounts" })
      .then(() => {
        updateButton();
        console.log("here");
        getAddress();
        window.location.replace(location.pathname);
      });
  }

  useEffect(() => {
    if (window.ethereum === undefined) return;

    let val = 0;
    if (val) {
      console.log("here");
      getAddress();
      toggleConnect(val);
      // updateButton();
    }

    window.ethereum.on("accountsChanged", function (accounts) {
      window.location.replace(location.pathname);
    });
  }, []);

  return (
    <>
      <nav className="navbar">
        <h3 className="logo">ApnaMarket.NFT</h3>
        {/*
        if large screen ma xa bhane Mobile add huxa
        if mobile screen ma xa bhane nav-links-mobile add huxa
        */}
        <ul
          className={Mobile ? "nav-links-mobile" : "nav-links"}
          onClick={() => setMobile(false)}
        >
          <Link to="/" className="marketplace">
            <li>Marketplace</li>
          </Link>
          <Link to="/SellNFT" className="sellnft">
            <li>Sell My NFT</li>
          </Link>
          <Link to="/Profile" className="profile">
            <li>Profile</li>
          </Link>

          <button
            className="enableEthereumButton text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 text-center mr-3 md:mr-0 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-200"
            onClick={connectWebsite}
          >
            {/* {connected ? "Connect" : "Connect Wallet"} */}
            <div className="text-white text-bold text-right  text-sm">
              {currAddress !== "0x"
                ? "Connected to"
                : "Wallet Not Connected..."}{" "}
              {currAddress !== "0x" ? currAddress.substring(0, 15) + "..." : ""}
            </div>
          </button>
        </ul>

        {/* 
        whenever we click on button = setMobile(!Mobile) ==  is mobile oppsite to setMobile 
        */}
      </nav>
      {/* <div className="text-white text-bold text-right mr-10 text-sm">
        {currAddress !== "0x" ? "Connected to" : "Not Connected..."}{" "}
        {currAddress !== "0x" ? currAddress.substring(0, 15) + "..." : ""}
      </div> */}
    </>
  );
}

export default Navbar;
